import React, {useState} from 'react';
import gql from "graphql-tag";
import { useQuery, useMutation } from "@apollo/react-hooks";

import HomeCard from '../components/HomeCard.js'

import Container from 'react-bootstrap/Container'
import CardDeck from 'react-bootstrap/CardDeck'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Form from 'react-bootstrap/Form'

  function Homes() {
    const [show, setShow] = useState(false);
    const [values, setValues] = useState({});

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const addHome = useMutation(ADD_HOME, {
      variables: values
    });

    function onSubmit(event){
      setValues({
        price: event.target.price.value,
        description: event.target.description.value,
        photo: event.target.photo.value,
        address: event.target.address.value
      })
    }

    const { loading, error, data }  = useQuery(GET_HOMES);

    if(loading) return 'Loading...';
    if(data != null)
      console.log(data.getHomes);
    return (
      <>
        {data && (
          <Container style={{paddingTop: '5rem'}}>
            <Row>
              <CardDeck>
                {data.getHomes.map((home, index) =>
                  <HomeCard key={index} description={home.description} address={home.address} price={home.price} photo={home.photo}/>
                )}
              </CardDeck>
            </Row>
            <Row>
              <Button onClick={handleShow}>
                Add Home
              </Button>
            </Row>

            <Modal show={show} onHide={handleClose}>
              <Form onSubmit={onSubmit}>
                <Modal.Header closeButton>
                  <Modal.Title>Add Home</Modal.Title>
                </Modal.Header>
                <Modal.Body>

                    <Form.Group controlId="price">
                      <Form.Label>Price</Form.Label>
                      <Form.Control type="number" placeholder="Enter price" />
                    </Form.Group>
                    <Form.Group controlId="description">
                      <Form.Label>description</Form.Label>
                      <Form.Control as="textarea" placeholder="Enter description" />
                    </Form.Group>
                    <Form.Group controlId="photo">
                      <Form.Label>Photo</Form.Label>
                      <Form.Control type="file" />
                    </Form.Group>
                    <Form.Group controlId="address">
                      <Form.Label>Address</Form.Label>
                      <Form.Control type="text" />
                    </Form.Group>

                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={handleClose}>
                    Close
                  </Button>
                  <Button variant="primary" type="submit">
                    Add
                  </Button>
                </Modal.Footer>
              </Form>
            </Modal>

          </Container>
        )}
      </>
    );
  }

const GET_HOMES = gql `
  {
    getHomes {
      address
      description
      price
      photo
    }
  }
`;

const ADD_HOME = gql`
  mutation register(
    $price: Int!
    $description: String!
    $photo: String!
    $address: String!
  ){
    addHome(
      homeInput:{
        price: $price
        description: $description
        photo: $photo
        address: $address
      }
    )
  }
`


export default Homes;
