import React, {useState} from 'react';
import gql from "graphql-tag";
import { useQuery } from "@apollo/react-hooks";

import HomeCard from '../components/HomeCard.js'

import Container from 'react-bootstrap/Container'
import CardDeck from 'react-bootstrap/CardDeck'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Form from 'react-bootstrap/Form'

  function Homes() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const { loading, error, data }  = useQuery(GET_HOMES);

    if(loading) return 'Loading...';
    if(data != null)
      console.log(data.getHomes);
    return (
      <>
        {data && (
          <Container style={{paddingTop: '5rem'}}>
            <Row>
              <CardDeck>
                {data.getHomes.map((home, index) =>
                  <HomeCard key={index} description={home.description} address={home.address} price={home.price} photo={home.photo}/>
                )}
              </CardDeck>
            </Row>
            <Row>
              <Button onClick={handleShow}>
                Add Home
              </Button>
            </Row>

            <Modal show={show} onHide={handleClose}>
              <Form>
                <Modal.Header closeButton>
                  <Modal.Title>Add Home</Modal.Title>
                </Modal.Header>
                <Modal.Body>

                    <Form.Group controlId="price">
                      <Form.Label>Price</Form.Label>
                      <Form.Control type="number" placeholder="Enter price" />
                    </Form.Group>
                    <Form.Group controlId="price">
                      <Form.Label>description</Form.Label>
                      <Form.Control as="textarea" placeholder="Enter description" />
                    </Form.Group>
                    <Form.Group controlId="price">
                      <Form.Label>Price</Form.Label>
                      <Form.Control type="image" placeholder="Enter price" />
                    </Form.Group>

                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={handleClose}>
                    Close
                  </Button>
                  <Button variant="primary" type="submit">
                    Save Changes
                  </Button>
                </Modal.Footer>
              </Form>
            </Modal>

          </Container>
        )}
      </>
    );
  }

const GET_HOMES = gql `
  {
    getHomes {
      address
      description
      price
      photo
    }
  }
`;


export default Homes;
